  &--primary {
    background-color: clr.$color-primary;

    & span {
      color: clr.$color-primary-light;
      margin-left: 0.25em;
    }

    &:hover,
    &:active {
      background-color: clr.$color-primary-lightest;
    }
  }

  &--secondary {
    background-color: clr.$color-secondary;

    & span {
      color: clr.$color-primary-light;
      margin-left: 0.25em;
    }

    &:hover,
    &:active {
      background-color: clr.$color-secondary-lightest;
    }
  }
}

